if (!starboardChannelId) return

const starEmoji = encodeURIComponent('â­'),
  starReaction = message.reactions.find(v => v.emoji === starEmoji)
const minCount = 3

if (!starReaction) return
if (starReaction.count < minCount) return

const getMessageTitle = (reactionCount) => {
  if (reactionCount < 6) return { emoji: 'â­', color: 'FFAC33' }
  if (reactionCount > 6 && reactionCount < 12) return { emoji: 'ðŸŒŸ', color: 'FFD983' }
  if (reactionCount > 12 && reactionCount < 24) return { emoji: 'ðŸ’«', color: 'FDD888' }
  if (reactionCount > 24 && reactionCount < 48) return { emoji: 'âœ¨', color: 'AA8ED6' }
  return { emoji: 'â˜„', color: 'BDDDF4' }
}

const messageImage = message.attachments.find(v => v.contentType.startsWith('image/'))
const messageTitle = getMessageTitle(starReaction.count),
  messageData = {
    content: `${messageTitle.emoji} **${starReaction.count}** | ${message.url}`,
    embeds: [
      {
        author: {
          name: member.user.username,
          icon_url: member.avatar
        },
        description: (message.content || message.url).slice(0, 2000),
        timestamp: Date.now(),
        color: parseInt(messageTitle.color, 16),
        image: messageImage ? { url: messageImage.url } : null
      }
    ]
  }

const starboardMessageId = await getValue(`starboard.${message.id}`)
if (starboardMessageId) {
  await editMessage(starboardChannelId, starboardMessageId, messageData)
} else {
  const starboardMessage = await sendMessage(starboardChannelId, messageData)
  setValue(`starboard.${message.id}`, starboardMessage.id)
}

const commendEmoji = encodeURIComponent('ðŸ‘'),
  commendReaction = message.reactions.find(v => v.emoji === commendEmoji && v.userId)

if (!commendReaction || commendReaction.userId === member.user.id) return

let commends = await getValue(`commends.${member.user.id}`)
if (!commends) {
  commends = { count: 0, users: {} }
  setValue(`commends.${member.user.id}`, commends)
}

const alreadyCommended = Object.keys(commends.users).some(userId => {
  const date = new Date(commends.users[userId])
  return userId === commendReaction.userId && Date.now() - date.getTime() < 1000 * 60 * 60 * 12
})

if (alreadyCommended) return

commends.count++
commends.users[commendReaction.userId] = Date.now()
setValue(`commends.${member.user.id}`, commends)
